import React,{useState,useEffect} from 'react';
import CreateBoard from '../Utils/CreateBoard';
import { revealed } from "../Utils/Reveal";
import Cell from './Cell';
import { toast,ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
function Board() {
    /* this.state={
        no_of_mines:"0"
    } */
     const[no_mines,updateMines]=useState(0); 
    const [grid,setGrid]=useState([]);
    const [nonMinecount,setNonMinecount]=useState(0);
    const [mineLocation,setmineLocation]=useState([]);
    const style={
        display : 'flex',
        flexDirection : 'row',
    }
    /* this.updateNumberOfMines=this.updateNumberOfMines.bind(this); */
    useEffect(()=>{
        freshBoard();
    },[]);
    const updateNumberOfMines = () => {
        /* this.setState({no_of_mines:no_mines}) */
        var no_of_mines=Document.getElementbyId("no-mines").value;
        const newBoard=CreateBoard(10,10,no_of_mines);
        setNonMinecount(10*10-20);
        setmineLocation(newBoard.mineLocation);
        setGrid(newBoard.board);
    }
    // Making freshboard atstart
    const freshBoard = () => {
        const newBoard=CreateBoard(10,10,20);
        setNonMinecount(10*10-20);
        setmineLocation(newBoard.mineLocation);
        setGrid(newBoard.board);
    }
    const updateFlag=(e,x,y)=>{
        e.preventDefault();
        // deep copy of the object
        let newGrid=JSON.parse(JSON.stringify(grid));
        newGrid[x][y].flagged=true;
        console.log(newGrid[x][y]);
        setGrid(newGrid);
    }
    const newfresh=()=>{
        freshBoard();
    }
    const revealcell=(x,y)=>{
        let newGrid=JSON.parse(JSON.stringify(grid));
        if(newGrid[x][y].value==="X"){
            toast.dark(' Clicked on Mine ,Try Again', { position: "top-center", autoClose: 1000, hideProgressBar: false, closeOnClick: true, pauseOnHover: true, draggable: true, progress: undefined, });
            for(let i=0;i<mineLocation.length;i++){
                newGrid[mineLocation[i][0]][mineLocation[i][1]].revealed=true;
            }
            setGrid(newGrid);
            setTimeout(newfresh,500);
        }
        if(nonMinecount===0){
            toast.success('Wohoo!!,You won',{ position: "top-center", autoClose: 1000, hideProgressBar: false, closeOnClick: true, pauseOnHover: true, draggable: true, progress: undefined, });
            setTimeout(newfresh,500);
        }
        else{
            let revealedboard=revealed(newGrid,x,y,nonMinecount);
            setGrid(revealedboard.arr);
            setNonMinecount(revealedboard.newNonMines);
        }
        
    }
    
    return (
      <div>
        <div className="parent" style={{width:'900px',padding:'20px',border: '1px solid rgba(98, 130, 158, 0.557)',borderRadius:'15px'}}>
            <div className='game-window' style={{display:'flex',}}>
              <div className='game'>
                  {/* <h3 style={{color:'white',textAlign:'center',fontSize:'30px',margin:'0px'}}>Non-Mines - {nonMinecount}</h3> */}
                  <ToastContainer></ToastContainer>
                  {grid.map((singlerow,index1)=>{
                      return (
                          <div style={style} key={index1}>
                              {singlerow.map((singlecol,index2)=>{
                              return  <Cell details={   singlecol} key={index2} updateFlag={updateFlag} revealcell={revealcell}/>
                              })}
                              
                          </div>
                      )
                  })}
                </div>
                <div style={{display:'flex',flexDirection:"column",justifyContent:'center',alignContent:'center',minWidth:'500px',color:'white',padding:"10px"}}>
                    <div>
                        <h1 style={{fontSize:'40px'}}>Minesweeper</h1>
                        <h2 style={{fontSize:'20px',marginTop:'30px'}}>Total size: 10 X 10</h2>
                        <h2 style={{fontSize:'20px',marginTop:'30px'}}>Number of mines: 20</h2>
                        <h2 style={{fontSize:'20px',marginTop:'30px'}}>Number of non mines: 80</h2>
                        <h2 style={{fontSize:'20px',marginTop:'30px'}}>Number of non mines left : {nonMinecount}</h2>
                    </div>
                    <hr/>
                    <div>
                        <h2  style={{fontSize:'20px',marginTop:'30px'}}>Set no of mines: </h2>
                        <div style={{display:'flex',justifyContent:'space-evenly'}}>
                            <input id='no-mines' type='number' max={"100"} min={"2"}style={{height:'30px',width:'100px',alignItems:'center'}}/>
                            <button onClick={updateNumberOfMines()}>Set</button>
                        </div>                        
                    </div>
                </div>
            </div>
          
        </div>
      </div>
    ) 
}
export default Board;
